import java.util.Set;
import java.util.HashSet;
import java.util.Queue;
import java.util.LinkedList;

public class day6 {
    public static int day6(String input, int n) {
        var inputArray = input.toCharArray();
        Set<Character> set = new HashSet<>(n);
        Queue<Character> queue = new LinkedList<>();
        char character, rem;
        for (int i = 0; i < inputArray.length; i++) {
            character = inputArray[i];
            queue.add(character);
            if (!set.add(character)) while ((rem = queue.remove()) != character) set.remove(rem);
            if (set.size() == n) return i + 1;
        }
        return -1;
    }
    public static void main(String[] args) {
        String input = "";
        System.out.println(day6(input, 4));
        System.out.println(day6(input, 14));
    }
}
